INSERT INTO `book` VALUES ('8', '中学教材全解', 'admin/kindeditor/attached/image/20141231/20141231165249_57658.jpg', '18', '薛金星', '中国出版社', '2014-09-08', '1', '205000', '平装', '<div class=\"tit\" style=\"margin:0px;padding:0px 0px 0px 17px;border:0px;font-weight:bold;font-size:14px;font-family:SimSun, Arial;background-color:#F5F5F5;\">\r\n	编辑推荐\r\n</div>\r\n<div class=\"descrip\" style=\"margin:0px;padding:25px 29px 15px;border:0px;font-size:14px;font-family:SimSun, Arial;background-color:#FFFFFF;\">\r\n	<span id=\"abstract_all\"> 《中学教材全解七年级语文上人教版》与最新教材完全同步 <br />\r\n《中学教材全解》系列丛书，自2000年上市以来，年年修订，年年出新，年年销量创新高它开创了教辅图书的新形态，在神州大地独树一帜，为广大师生增添了新的选择，给图书市场增加了新的亮点，创造了教辅图书的新神话！目前同类教辅产品已不下百种，但金星教育的《中学教材全解》在市场上独占鳘头。金星教育能够取得这样骄人的业绩，首先得益于“全心全意，解疑解难”的编写理念，其次得益于“全、细、精、透、新、实、活、巧”的编写原则，再次得益于“想学生所想，急教师所急，忧家长所忧”的服务宗旨，更得益于强烈的责任意识和质量意识。对学生、对老师、对家长、对教育事业负责是金星教育追求高质量的不竭动力！用金星教辅，走成才之路，做教师的助手，为学子的益友! </span> \r\n</div>', '中学教材');
